import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AboutUsPage.css';
import mangoBackground from '../../assets/differentmango.jpg';
import mangoLogo from '../../assets/Logo_white.png';
import pythonLogo from '../../assets/python.png';
import pytorchLogo from '../../assets/pytorch.png';
import tensorflowLogo from '../../assets/tensorflow.png';

function AboutUsPage() {
    const navigate = useNavigate();

    useEffect(() => {
        const observer = new IntersectionObserver((entries) => {
            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('animate');
                }
            });
        });

        const elements = document.querySelectorAll('.content-section, .framework-item');
        elements.forEach(element => observer.observe(element));

        return () => {
            elements.forEach(element => observer.unobserve(element));
        };
    }, []);

    return (
        <div className="aboutus-page-container">
            <nav className="aboutus-navbar">
                <img src={mangoLogo} alt="Mango Logo" className="navbar-logo" onClick={() => navigate('/mainhomepage')} />
                <button className="navbar-button" onClick={() => navigate('/signin')}>Sign-In</button>
            </nav>

            <header className="aboutus-hero" style={{ backgroundImage: `url(${mangoBackground})` }}>
                <h1 className="hero-title">About Us</h1>
            </header>

            <main className="aboutus-content">
                <section className="content-section">
                    <h2 className="content-title">Mangoers</h2>
                    <p className="content-description">
                        Mangoers Bruise Tracker, developed at Mae Fah Luang University, specializes in precise mango bruise detection.
                    </p>
                </section>

                <section className="frameworks-section">
                    <h3 className="frameworks-title">Frameworks We Use</h3>
                    <div className="frameworks-logos">
                        {[{ logo: pythonLogo, name: "Python" }, { logo: pytorchLogo, name: "PyTorch" }, { logo: tensorflowLogo, name: "TensorFlow" }]
                            .map((item, index) => (
                                <div key={index} className="framework-item">
                                    <img src={item.logo} alt={item.name} className="framework-logo" />
                                    <p>{item.name}</p>
                                </div>
                            ))}
                    </div>
                </section>
            </main>

            <footer className="aboutus-footer">
                <div className="footer-links">
                    <button className="footer-link" onClick={() => navigate('/aboutuspage')}>About Us</button>
                    <button className="footer-link" onClick={() => navigate('/contactuspage')}>Contact Us</button>
                </div>
                <p className="footer-address">Mae Fah Luang University, Chiang Rai</p>
            </footer>
        </div>
    );
}

export default AboutUsPage;
