import React, { useState, useCallback, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Resize.css';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';
import JSZip from 'jszip';
import { saveAs } from 'file-saver';

const Resize = () => {
    const [dragActive, setDragActive] = useState(false);
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [resizedImages, setResizedImages] = useState([]);
    const [dimensions, setDimensions] = useState({ width: '', height: '' });
    const [keepAspectRatio, setKeepAspectRatio] = useState(false);

    const navigate = useNavigate();

    const handleNavigation = useCallback((path) => navigate(path), [navigate]);

    const handleFileChange = useCallback((event) => {
        const files = Array.from(event.target.files).filter(file => /\.(jpg|jpeg|png)$/i.test(file.name));
        if (files.length > 0) {
            setSelectedFiles(prevFiles => [...prevFiles, ...files]);
        } else {
            alert('Only .jpg, .jpeg, and .png files are allowed.');
        }
    }, []);

    const handleResizeImages = useCallback(() => {
        if (!dimensions.width || !dimensions.height) {
            alert('Please enter both width and height!');
            return;
        }

        const resized = selectedFiles.map(file => ({
            name: file.name,
            width: dimensions.width,
            height: dimensions.height,
        }));

        setResizedImages(resized);
    }, [dimensions, selectedFiles]);

    const handleDownloadImage = useCallback((image) => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const img = new Image();

        img.onload = () => {
            canvas.width = image.width;
            canvas.height = image.height;
            ctx.drawImage(img, 0, 0, image.width, image.height);

            canvas.toBlob((blob) => {
                const url = URL.createObjectURL(blob);
                const a = document.createElement('a');
                a.href = url;
                a.download = image.name;
                document.body.appendChild(a);
                a.click();
                document.body.removeChild(a);
                URL.revokeObjectURL(url);
            }, 'image/jpeg');
        };

        img.src = URL.createObjectURL(selectedFiles.find(file => file.name === image.name));
    }, [selectedFiles]);

    const handleDownloadAllImages = useCallback(async () => {
        const zip = new JSZip();
        for (const image of resizedImages) {
            const imgBlob = await new Promise((resolve) => {
                const canvas = document.createElement('canvas');
                const ctx = canvas.getContext('2d');
                const img = new Image();
                img.onload = () => {
                    canvas.width = image.width;
                    canvas.height = image.height;
                    ctx.drawImage(img, 0, 0, image.width, image.height);
                    canvas.toBlob(resolve, 'image/jpeg');
                };
                img.src = URL.createObjectURL(
                    selectedFiles.find((file) => file.name === image.name)
                );
            });
            zip.file(image.name, imgBlob);
        }
        const content = await zip.generateAsync({ type: 'blob' });
        saveAs(content, 'resized-images.zip');
    }, [resizedImages, selectedFiles]);

    const handleReset = useCallback(() => {
        setSelectedFiles([]);
        setResizedImages([]);
        setDimensions({ width: '', height: '' });
    }, []);

    useEffect(() => {
        if (keepAspectRatio && dimensions.width && dimensions.height) {
            const aspectRatio = dimensions.width / dimensions.height;
            setDimensions(prev => ({
                width: prev.width || Math.round(prev.height * aspectRatio),
                height: prev.height || Math.round(prev.width / aspectRatio),
            }));
        }
    }, [keepAspectRatio, dimensions]);

    return (
        <div className="bruiseareacalculation-page">
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
                </div>
                <div className="navbar-links">
                    <button onClick={() => handleNavigation('/dashboardpage')}>Dashboard</button>
                    <button onClick={() => handleNavigation('/bruiseareacalculation')}>Bruised Area Calculation</button>
                    <button onClick={() => handleNavigation('/featureanalysis')}>Feature Analysis</button>
                    <button>Resize</button>
                    <button onClick={() => handleNavigation('/aboutuspage')}>About Us</button>
                    <button onClick={() => handleNavigation('/contactuspage')}>Contact Us</button>
                </div>
                <div className="navbar-profile" onClick={() => handleNavigation('/userprofilepage')}>
                    <img src={userProfileImg} alt="User Profile" className="user-profile" />
                </div>
            </nav>

            <div className="bruiseareacalculation-content">
                <h1>Resize Image</h1>
                <p>Resize JPG, PNG by defining new height and width pixels.</p>
                <div className="file-upload-container">
                    <div
                        className={`file-dropzone ${dragActive ? 'drag-active' : ''}`}
                        onDragOver={(e) => { e.preventDefault(); setDragActive(true); }}
                        onDragLeave={() => setDragActive(false)}
                        onDrop={(e) => {
                            e.preventDefault();
                            setDragActive(false);
                            handleFileChange({ target: { files: e.dataTransfer.files } });
                        }}
                    >
                        <p>Drag & Drop your images here or</p>
                        <input
                            type="file"
                            className="file-input"
                            accept=".jpg,.jpeg,.png"
                            multiple
                            onChange={handleFileChange}
                        />
                        <label>Browse Files</label>
                    </div>
                    <div className="resize-settings">
                        <h3>Resize Settings:</h3>
                        <input
                            type="number"
                            placeholder="Width (px)"
                            value={dimensions.width}
                            onChange={(e) => setDimensions(prev => ({ ...prev, width: e.target.value }))}
                        />
                        <input
                            type="number"
                            placeholder="Height (px)"
                            value={dimensions.height}
                            onChange={(e) => setDimensions(prev => ({ ...prev, height: e.target.value }))}
                        />
                        <label>
                            <input
                                type="checkbox"
                                checked={keepAspectRatio}
                                onChange={() => setKeepAspectRatio(!keepAspectRatio)}
                            />
                            Keep Aspect Ratio
                        </label>
                    </div>
                </div>

                <div className="action-buttons">
                    <button onClick={handleReset}>Reset</button>
                    <button onClick={handleResizeImages}>Resize</button>
                </div>

                {resizedImages.length > 0 && (
                    <div className="resized-images">
                        <h3>Resized Images:</h3>
                        {resizedImages.map((image, index) => (
                            <div key={index}>
                                <p>{image.name} - {image.width}px x {image.height}px</p>
                                <button onClick={() => handleDownloadImage(image)}>Download</button>
                            </div>
                        ))}
                        <button onClick={handleDownloadAllImages}>Download All as ZIP</button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Resize;
