import React, { useState, useCallback } from 'react';
import './CropImage.css';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';

const CropImage = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);

    const handleFileChange = useCallback((event) => {
        const file = event.target.files[0];
        if (file && /\.(jpg|jpeg|png)$/i.test(file.name)) {
            setSelectedFile(file);
            const reader = new FileReader();
            reader.onload = () => setImagePreview(reader.result);
            reader.readAsDataURL(file);
        } else {
            alert('Only .jpg, .jpeg, and .png files are allowed.');
        }
    }, []);

    const handleClear = useCallback(() => {
        setSelectedFile(null);
        setImagePreview(null);
    }, []);

    return (
        <div className="cropimage-page">
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
                </div>
                <div className="navbar-links">
                    <button className="navbar-link">Dashboard</button>
                    <button className="navbar-link">Crop Image</button>
                </div>
                <div className="navbar-profile">
                    <img src={userProfileImg} alt="User Profile" className="user-profile" />
                </div>
            </nav>

            <div className="cropimage-content">
                <h1 className="cropimage-title">Crop Image</h1>
                <p className="cropimage-description">
                    Upload your image to crop and adjust it for your needs.
                </p>

                <div className="file-upload-container">
                    {!imagePreview ? (
                        <div className="file-upload">
                            <input
                                type="file"
                                className="file-input"
                                id="file-upload"
                                onChange={handleFileChange}
                                accept=".jpg,.jpeg,.png"
                            />
                            <label htmlFor="file-upload" className="browse-btn">
                                Browse 📁
                            </label>
                            <p className="file-requirements">
                                🚨 Only .jpg, .jpeg, and .png files are allowed.
                            </p>
                        </div>
                    ) : (
                        <div className="image-preview-container">
                            <img src={imagePreview} alt="Preview" className="image-preview" />
                        </div>
                    )}
                </div>

                <div className="action-buttons">
                    {imagePreview && (
                        <>
                            <button className="clear-btn" onClick={handleClear}>
                                Clear Image
                            </button>
                            <button className="crop-btn">
                                Crop Image
                            </button>
                        </>
                    )}
                </div>
            </div>

            <footer className="footer">
                <div className="footer-address">
                    <p>Mae Fah Luang University 333 Moo 1, Thasud, Muang, Chiang Rai 57100</p>
                </div>
            </footer>
        </div>
    );
};

export default CropImage;
