import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import './MainHomePage.css';
import mangoBackground from '../../assets/differentmango.jpg';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';
import { UserContext } from '../../UserContext';

function MainHomePage() {
    const navigate = useNavigate();
    const { userInfo } = useContext(UserContext);

    const handleNavigation = (path) => () => navigate(path);

    return (
        <div className="main-homepage">
            {/* Navbar */}
            <nav className="navbar-mainhomepage">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="manger-logo" />
                </div>

                <div className="navbar-links">
                    <button className="navbar-link" onClick={handleNavigation('/dashboardpage')}>Dashboard</button>
                    <button className="navbar-link" onClick={handleNavigation('/bruiseareacalculation')}>Bruised Area Calculation</button>
                    <button className="navbar-link" onClick={handleNavigation('/featureanalysis')}>Feature Analysis</button>
                    <button className="navbar-link">Resize</button>
                    <button className="navbar-link" onClick={handleNavigation('/aboutuspage')}>About Us</button>
                    <button className="navbar-link" onClick={handleNavigation('/contactuspage')}>Contact Us</button>
                </div>

                <div className="navbar-profile">
                    <img 
                        src={userInfo.profileImage || userProfileImg} 
                        alt="User Profile" 
                        className="user-profile" 
                        onClick={handleNavigation('/userprofilepage')} 
                    />
                </div>
            </nav>

            {/* Hero Section */}
            <div className="hero-section" style={{ backgroundImage: `url(${mangoBackground})` }}>
                <div className="hero-content">
                    <h1>Welcome to Mangoers Bruise Tracker!</h1>
                    <p>
                        In the agricultural industry, finding bruised areas on mangoes with just the naked eye can be challenging, 
                        as many bruises are not easily visible. These hidden bruises can reduce the quality and market value of the fruit. 
                        The project Bruise Tracker aims to solve this problem by using computer vision technology to accurately detect 
                        and measure bruised areas that are hard to see. By providing precise calculations of the bruise-to-fruit ratio, 
                        bruise tracker offers important benefits for quality control and marketability. This technology automates the 
                        bruise detection process, making it more efficient, accurate, and reliable, and has the potential to improve 
                        fruit selection and reduce waste in the agricultural sector.
                    </p>
                </div>
            </div>

            {/* Footer */}
            <footer className="footer-mainhomepage">
                <div className="footer-address">
                    <p>Mae Fah Luang University 333 Moo 1, Thasud, Muang, Chiang Rai 57100</p>
                </div>
            </footer>
        </div>
    );
}

export default MainHomePage;
