import React, { useState, useCallback, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Resize.css';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';
import JSZip from "jszip";
import { saveAs } from "file-saver";
import Cropper from 'react-easy-crop'; // Install this package: npm install react-easy-crop
import getCroppedImg from './cropImageUtils'; // Add a utility file for cropping logic

const Resize = () => {
    const [dragActive, setDragActive] = useState(false);
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [croppedImages, setCroppedImages] = useState([]);
    const [crop, setCrop] = useState({ x: 0, y: 0 });
    const [zoom, setZoom] = useState(1);
    const [currentFile, setCurrentFile] = useState(null);
    const [cropArea, setCropArea] = useState(null);
    const [showCropper, setShowCropper] = useState(false);

    const navigate = useNavigate();
    const handleNavigation = useCallback((path) => () => navigate(path), [navigate]);

    const handleFileChange = useCallback((event) => {
        const files = Array.from(event.target.files);
        const validFiles = files.filter(file => /\.(jpg|jpeg|png)$/i.test(file.name));
        if (validFiles.length > 0) {
            setSelectedFiles(prevFiles => [...prevFiles, ...validFiles]);
        } else {
            alert('Only .jpg, .jpeg, and .png files are allowed.');
        }
    }, []);

    const onCropComplete = useCallback(async (_, croppedAreaPixels) => {
        setCropArea(croppedAreaPixels);
    }, []);

    const handleCropImage = useCallback(async () => {
        if (!currentFile || !cropArea) return;

        const croppedImage = await getCroppedImg(
            URL.createObjectURL(currentFile),
            cropArea
        );

        setCroppedImages((prev) => [...prev, { name: currentFile.name, url: croppedImage }]);
        setShowCropper(false);
        setCurrentFile(null);
    }, [currentFile, cropArea]);

    const handleCropButtonClick = useCallback((file) => {
        setCurrentFile(file);
        setShowCropper(true);
    }, []);

    const handleReset = useCallback(() => {
        setSelectedFiles([]);
        setCroppedImages([]);
        setCrop({ x: 0, y: 0 });
        setZoom(1);
        setCurrentFile(null);
        setShowCropper(false);
    }, []);

    return (
        <div className="bruiseareacalculation-page">
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
                </div>
                <div className="navbar-links">
                    <button className="navbar-link" onClick={handleNavigation('/dashboardpage')}>Dashboard</button>
                    <button className="navbar-link" onClick={handleNavigation('/bruiseareacalculation')}>Bruised Area Calculation</button>
                    <button className="navbar-link" onClick={handleNavigation('/featureanalysis')}>Feature Analysis</button>
                    <button className="navbar-link">Resize</button>
                    <button className="navbar-link" onClick={handleNavigation('/aboutuspage')}>About Us</button>
                    <button className="navbar-link" onClick={handleNavigation('/contactuspage')}>Contact Us</button>
                </div>
                <div className="navbar-profile" onClick={handleNavigation('/userprofilepage')}>
                    <img src={userProfileImg} alt="User Profile" className="user-profile" />
                </div>
            </nav>

            <div className="bruiseareacalculation-content">
                <h1 className="bruiseareacalculation-title">Crop & Resize Image</h1>
                <p className="bruise-description">
                    Crop and resize JPG, PNG images by defining crop areas and dimensions. Change image dimensions easily.
                </p>
                <div className="file-upload-container">
                    <div
                        className={`file-dropzone ${dragActive ? 'drag-active' : ''}`}
                        onDragOver={(e) => { e.preventDefault(); setDragActive(true); }}
                        onDragLeave={() => setDragActive(false)}
                        onDrop={(e) => {
                            e.preventDefault();
                            setDragActive(false);
                            const files = Array.from(e.dataTransfer.files);
                            handleFileChange({ target: { files } });
                        }}
                    >
                        <p>Drag & Drop your images here or</p>
                        <input
                            type="file"
                            className="file-input"
                            id="file-upload"
                            onChange={handleFileChange}
                            accept=".jpg,.jpeg,.png"
                            multiple
                        />
                        <label htmlFor="file-upload" className="browse-bruiseareacalculation-btn">
                            Browse 📁
                        </label>
                        <p>🚨 Only .jpg .jpeg .png files are allowed</p>
                    </div>
                </div>

                {selectedFiles.length > 0 && (
                    <div className="selected-files-box">
                        <h3>Selected Files:</h3>
                        <ul>
                            {selectedFiles.map((file, index) => (
                                <li key={index}>
                                    {file.name}
                                    <button onClick={() => handleCropButtonClick(file)}>Crop</button>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {showCropper && currentFile && (
                    <div className="cropper-container">
                        <Cropper
                            image={URL.createObjectURL(currentFile)}
                            crop={crop}
                            zoom={zoom}
                            aspect={4 / 3}
                            onCropChange={setCrop}
                            onZoomChange={setZoom}
                            onCropComplete={onCropComplete}
                        />
                        <button onClick={handleCropImage}>Apply Crop</button>
                        <button onClick={() => setShowCropper(false)}>Cancel</button>
                    </div>
                )}

                {croppedImages.length > 0 && (
                    <div className="cropped-images">
                        <h3>Cropped Images:</h3>
                        <ul>
                            {croppedImages.map((image, index) => (
                                <li key={index}>
                                    <img src={image.url} alt={image.name} />
                                    <p>{image.name}</p>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Resize;
