import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Resize.css";
import mangoLogo from "../../assets/Logo_white.png";
import userProfileImg from "../../assets/profile.jpg";

const Resize = () => {
  const [dragActive, setDragActive] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const navigate = useNavigate();

  const handleNavigation = (path) => navigate(path);

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    const validFiles = files.filter((file) =>
      /\.(jpg|jpeg|png)$/i.test(file.name)
    );
    if (validFiles.length > 0) {
      setSelectedFiles((prevFiles) => [...prevFiles, ...validFiles]);
    } else {
      alert("Only .jpg, .jpeg, and .png files are allowed.");
    }
  };

  const handleDragEvents = (event, isActive) => {
    event.preventDefault();
    setDragActive(isActive);
  };

  const handleFileDelete = (index) => {
    setSelectedFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
  };

  const handleResize = () => {
    if (selectedFiles.length === 0) {
      alert("Please upload at least one file to resize.");
      return;
    }
    // Perform resize logic or navigate to another page
    alert("Resize functionality coming soon!");
  };

  return (
    <div className="resize-page">
      <nav className="navbar">
        <div className="navbar-brand">
          <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
        </div>
        <div className="navbar-links">
          <button onClick={() => handleNavigation("/dashboard")}>
            Dashboard
          </button>
          <button>Resize</button>
          <button onClick={() => handleNavigation("/aboutus")}>About Us</button>
          <button onClick={() => handleNavigation("/contactus")}>
            Contact Us
          </button>
        </div>
        <div
          className="navbar-profile"
          onClick={() => handleNavigation("/userprofile")}
        >
          <img
            src={userProfileImg}
            alt="User Profile"
            className="user-profile"
          />
        </div>
      </nav>

      <div className="resize-content">
        <h1>Resize Images</h1>
        <p>Upload images to resize and optimize them for your needs.</p>

        <div
          className={`file-dropzone ${dragActive ? "drag-active" : ""}`}
          onDragOver={(e) => handleDragEvents(e, true)}
          onDragLeave={(e) => handleDragEvents(e, false)}
          onDrop={(e) => {
            handleDragEvents(e, false);
            handleFileChange(e);
          }}
        >
          <p>Drag & Drop your images here or</p>
          <input
            type="file"
            id="file-upload"
            className="file-input"
            accept=".jpg,.jpeg,.png"
            multiple
            onChange={handleFileChange}
          />
          <label htmlFor="file-upload" className="browse-btn">
            Browse Files
          </label>
        </div>

        {selectedFiles.length > 0 && (
          <div className="selected-files-box">
            <h3>Selected Files:</h3>
            <ul>
              {selectedFiles.map((file, index) => (
                <li key={index} className="file-item">
                  {file.name} ({(file.size / 1024).toFixed(2)} KB)
                  <button
                    onClick={() => handleFileDelete(index)}
                    className="delete-btn"
                  >
                    ❌
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}

        <div className="action-buttons">
          <button
            onClick={() => handleNavigation("/dashboard")}
            className="back-btn"
          >
            Back
          </button>
          <button onClick={handleResize} className="resize-btn">
            Resize
          </button>
        </div>
      </div>

      <footer className="footer">
        <p>
          Mae Fah Luang University 333 Moo 1, Thasud, Muang, Chiang Rai 57100
        </p>
      </footer>
    </div>
  );
};

export default Resize;
