import React, { useState } from "react";
import Cropper from "react-easy-crop";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Footer } from "../components/Footer";
import { Navbar } from "../components/Navbar";
import { getCroppedImg } from "../utils/cropImage";
import "./CropImage.css";

const CropImage = () => {
  const [image, setImage] = useState(null);
  const [croppedArea, setCroppedArea] = useState(null);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedImage, setCroppedImage] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => setImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleCropComplete = (croppedArea, croppedAreaPixels) => {
    setCroppedArea(croppedAreaPixels);
  };

  const handleCrop = async () => {
    try {
      const croppedImg = await getCroppedImg(image, croppedArea);
      setCroppedImage(croppedImg);
    } catch (error) {
      console.error("Crop failed: ", error);
    }
  };

  return (
    <div className="crop-page">
      <Navbar />

      <Card className="crop-container">
        <CardContent>
          <h1 className="text-2xl font-bold mb-4">Crop Image</h1>
          {!image && (
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              className="mb-4"
            />
          )}

          {image && (
            <div className="crop-wrapper">
              <Cropper
                image={image}
                crop={crop}
                zoom={zoom}
                aspect={1}
                onCropChange={setCrop}
                onZoomChange={setZoom}
                onCropComplete={handleCropComplete}
              />
            </div>
          )}

          <div className="button-group mt-4">
            {image && (
              <Button onClick={handleCrop} className="mr-2">
                Crop
              </Button>
            )}

            {croppedImage && (
              <a href={croppedImage} download="cropped-image.jpg">
                <Button>Download Cropped Image</Button>
              </a>
            )}
          </div>
        </CardContent>
      </Card>

      <Footer />
    </div>
  );
};

export default CropImage;
