import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Cropper from 'react-easy-crop';
import './CropImage.css';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';

const CropImage = () => {
    const [dragActive, setDragActive] = useState(false);
    const [selectedImage, setSelectedImage] = useState(null);
    const [crop, setCrop] = useState({ x: 0, y: 0 });
    const [zoom, setZoom] = useState(1);
    const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
    const navigate = useNavigate();

    const handleAboutUs = useCallback(() => navigate('/aboutuspage'), [navigate]);
    const handleContactUs = useCallback(() => navigate('/contactuspage'), [navigate]);
    const handleUserProfile = useCallback(() => navigate('/userprofilepage'), [navigate]);
    const handleDashboard = useCallback(() => navigate('/dashboardpage'), [navigate]);

    const onCropComplete = useCallback((_, croppedAreaPixels) => {
        setCroppedAreaPixels(croppedAreaPixels);
    }, []);

    const handleFileChange = useCallback((event) => {
        const file = event.target.files[0];
        if (file && /\.(jpg|jpeg|png)$/i.test(file.name)) {
            const reader = new FileReader();
            reader.onload = () => {
                setSelectedImage(reader.result);
            };
            reader.readAsDataURL(file);
        } else {
            alert('Only .jpg, .jpeg, and .png files are allowed.');
        }
    }, []);

    const handleCropSave = useCallback(() => {
        alert(`Cropped area: ${JSON.stringify(croppedAreaPixels)}`);
        // Implement logic to crop the image using croppedAreaPixels and save it
    }, [croppedAreaPixels]);

    return (
        <div className="cropimage-page">
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
                </div>
                <div className="navbar-links">
                    <button className="navbar-link" onClick={handleDashboard}>Dashboard</button>
                    <button className="navbar-link" onClick={handleAboutUs}>About Us</button>
                    <button className="navbar-link" onClick={handleContactUs}>Contact Us</button>
                </div>
                <div className="navbar-profile" onClick={handleUserProfile}>
                    <img src={userProfileImg} alt="User Profile" className="user-profile" />
                </div>
            </nav>

            <div className="cropimage-content">
                <h1 className="cropimage-title">Crop Image</h1>
                <p className="description">
                    Select and crop your image to the perfect size.
                </p>
                <div className="file-upload-container">
                    {!selectedImage ? (
                        <div className="file-dropzone">
                            <input
                                type="file"
                                className="file-input"
                                id="file-upload"
                                onChange={handleFileChange}
                                accept=".jpg,.jpeg,.png"
                            />
                            <label htmlFor="file-upload" className="browse-btn">
                                Upload Image
                            </label>
                        </div>
                    ) : (
                        <div className="crop-container">
                            <Cropper
                                image={selectedImage}
                                crop={crop}
                                zoom={zoom}
                                aspect={4 / 3}
                                onCropChange={setCrop}
                                onZoomChange={setZoom}
                                onCropComplete={onCropComplete}
                            />
                            <div className="crop-controls">
                                <input
                                    type="range"
                                    value={zoom}
                                    min={1}
                                    max={3}
                                    step={0.1}
                                    onChange={(e) => setZoom(e.target.value)}
                                />
                            </div>
                        </div>
                    )}
                </div>

                {selectedImage && (
                    <div className="action-buttons">
                        <button className="btn cancel-btn" onClick={() => setSelectedImage(null)}>
                            Cancel
                        </button>
                        <button className="btn save-btn" onClick={handleCropSave}>
                            Save Crop
                        </button>
                    </div>
                )}
            </div>

            <footer className="footer">
                <div className="footer-address">
                    <p>Mae Fah Luang University 333 Moo 1, Thasud, Muang, Chiang Rai 57100</p>
                </div>
            </footer>
        </div>
    );
};

export default CropImage;
