import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Cropper from 'react-easy-crop'; // Ensure this is installed
import './CropImage.css';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';

const CropImage = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const navigate = useNavigate();

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && /\.(jpg|jpeg|png)$/i.test(file.name)) {
      const reader = new FileReader();
      reader.onload = () => setSelectedImage(reader.result);
      reader.readAsDataURL(file);
    } else {
      alert('Only .jpg, .jpeg, and .png files are allowed.');
    }
  };

  const handleUpload = () => {
    alert('Image cropped and ready for upload!');
  };

  return (
    <div className="page-container">
      <header className="navbar">
        <img src={mangoLogo} alt="Mango Logo" className="logo" />
        <img src={userProfileImg} alt="User Profile" className="user-profile" />
      </header>

      <main className="main-content">
        <h1>Crop Your Image</h1>
        <p>Resize and crop your image in a simple and easy way.</p>

        {!selectedImage ? (
          <div className="upload-container">
            <input
              type="file"
              id="file-upload"
              onChange={handleFileChange}
              accept=".jpg,.jpeg,.png"
            />
            <label htmlFor="file-upload">Upload Image</label>
          </div>
        ) : (
          <div className="crop-container">
            <Cropper
              image={selectedImage}
              crop={crop}
              zoom={zoom}
              aspect={4 / 3}
              onCropChange={setCrop}
              onZoomChange={setZoom}
            />
            <input
              type="range"
              value={zoom}
              min={1}
              max={3}
              step={0.1}
              onChange={(e) => setZoom(Number(e.target.value))}
              className="zoom-control"
            />
          </div>
        )}

        <div className="action-buttons">
          <button onClick={handleUpload} className="upload-btn">
            Update Image
          </button>
        </div>
      </main>

      <footer className="footer">
        <p>Mae Fah Luang University, Chiang Rai 57100</p>
      </footer>
    </div>
  );
};

export default CropImage;
