import React, { useState, useCallback, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './CropImage.css';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';
import JSZip from "jszip";
import { saveAs } from "file-saver";
import CropImageComponent from './CropImage'; // Import the CropImage component

const CropImagePage = () => {
    const [dragActive, setDragActive] = useState(false);
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [resizedImages, setResizedImages] = useState([]);
    const [dimensions, setDimensions] = useState({ width: '', height: '' });
    const [cropImage, setCropImage] = useState(null); // State for cropped image

    const navigate = useNavigate();

    // Navigation handlers
    const handleCropImage = useCallback(() => {
        navigate('/cropimage'); // Navigate to the CropImage page
    }, [navigate]);

    const handleFileChange = useCallback((event) => {
        const files = Array.from(event.target.files);
        const validFiles = files.filter(file => /\.(jpg|jpeg|png)$/i.test(file.name));

        if (validFiles.length > 0) {
            setSelectedFiles(prevFiles => [...prevFiles, ...validFiles]);
        } else {
            alert('Only .jpg, .jpeg, and .png files are allowed.');
        }
    }, []);

    const handleResizeImages = useCallback(() => {
        if (!dimensions.width || !dimensions.height) {
            alert('Please enter both width and height!');
            return;
        }

        const resized = selectedFiles.map(file => ({
            name: file.name,
            width: dimensions.width,
            height: dimensions.height,
        }));

        setResizedImages(resized);
    }, [dimensions, selectedFiles]);

    const handleImageCropComplete = (croppedImage) => {
        setCropImage(croppedImage); // Update state with the cropped image
    };

    return (
        <div className="bruiseareacalculation-page">
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
                </div>
                <div className="navbar-links">
                    <button className="navbar-link" onClick={handleCropImage}>Crop Image</button>
                </div>
                <div className="navbar-profile">
                    <img src={userProfileImg} alt="User Profile" className="user-profile" />
                </div>
            </nav>

            <div className="bruiseareacalculation-content">
                <h1 className="bruiseareacalculation-title">Resize Image</h1>
                <p className="bruise-description">
                    Resize JPG, PNG, SVG or GIF by defining new height and width pixels. Change image dimensions in bulk.
                </p>
                <div className="file-upload-container">
                    <div
                        className={`file-dropzone ${dragActive ? 'drag-active' : ''}`}
                        onDragOver={(e) => { e.preventDefault(); setDragActive(true); }}
                        onDragLeave={() => setDragActive(false)}
                        onDrop={(e) => {
                            e.preventDefault();
                            setDragActive(false);
                            const files = Array.from(e.dataTransfer.files);
                            handleFileChange({ target: { files } });
                        }}
                    >
                        <p>Drag & Drop your images here or</p>
                        <input
                            type="file"
                            className="file-input"
                            id="file-upload"
                            onChange={handleFileChange}
                            accept=".jpg,.jpeg,.png"
                            multiple
                        />
                        <label htmlFor="file-upload" className="browse-bruiseareacalculation-btn">
                            Browse 📁
                        </label>
                    </div>

                    {selectedFiles.length > 0 && (
                        <div className="uploaded-image-box">
                            <h3>Uploaded Images:</h3>
                            {selectedFiles.map((file, index) => (
                                <div key={index}>
                                    <img
                                        src={URL.createObjectURL(file)}
                                        alt={file.name}
                                        className="uploaded-image-preview"
                                    />
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                {selectedFiles.length > 0 && (
                    <div className="crop-container">
                        <cropImage imageSrc={URL.createObjectURL(selectedFiles[0])} onCropComplete={handleImageCropComplete} />
                    </div>
                )}

                <div className="action-buttons">
                    <button className="bt upload-bruiseareacalculation-bt" onClick={handleResizeImages}>
                        Resize
                    </button>
                </div>

                {cropImage && (
                    <div className="cropped-image-box">
                        <h3>Cropped Image:</h3>
                        <img src={cropImage} alt="Cropped" className="cropped-image-preview" />
                    </div>
                )}
            </div>
        </div>
    );
};

export default CropImagePage;
