import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CropImage.css';
import mangoLogo from '../../assets/Logo_white.png';

const CropImage = () => {
    const [image, setImage] = useState(null);
    const [zoom, setZoom] = useState(1);
    const navigate = useNavigate();

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = () => setImage(reader.result);
            reader.readAsDataURL(file);
        }
    };

    const handleUpload = () => {
        if (image) {
            // Simulate upload functionality or further processing
            navigate('/showareacalculation');
        } else {
            alert('Please upload an image first.');
        }
    };

    return (
        <div className="bruiseareacalculation-page">
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
                </div>
            </nav>

            <div className="bruiseareacalculation-content">
                <h1 className="bruiseareacalculation-title">Bruise Area Calculation</h1>
                <p className="bruise-description">
                    The Bruise area calculation will show the total area of the mango and the total bruise area.
                </p>
                <div className="crop-image-container">
                    <div className="image-upload">
                        <label htmlFor="imageInput" className="upload-label">Upload Image</label>
                        <input
                            type="file"
                            id="imageInput"
                            accept="image/*"
                            onChange={handleImageUpload}
                            hidden
                        />
                    </div>

                    {image && (
                        <div className="image-crop">
                            <div className="crop-box">
                                <img
                                    src={image}
                                    alt="Crop Preview"
                                    style={{ transform: `scale(${zoom})` }}
                                />
                            </div>
                            <div className="crop-controls">
                                <label>Zoom:</label>
                                <input
                                    type="range"
                                    min="1"
                                    max="3"
                                    step="0.1"
                                    value={zoom}
                                    onChange={(e) => setZoom(e.target.value)}
                                />
                            </div>
                        </div>
                    )}
                </div>

                <div className="action-buttons">
                    <button className="bt backto-bt" onClick={() => navigate('/dashboardpage')}>
                        Back
                    </button>
                    <button className="bt upload-bruiseareacalculation-bt" onClick={handleUpload}>
                        Upload
                    </button>
                </div>
            </div>

            <footer className="footer-bruiseareacalculation">
                <p>Mae Fah Luang University 333 Moo 1, Thasud, Muang, Chiang Rai 57100</p>
            </footer>
        </div>
    );
};

export default CropImage;
