import React, { useState, useCallback, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Resize.css';
import mangoLogo from '../../assets/Logo_white.png';
import userProfileImg from '../../assets/profile.jpg';
import JSZip from 'jszip';
import { saveAs } from 'file-saver';

const Resize = () => {
    const [dragActive, setDragActive] = useState(false);
    const [selectedFiles, setSelectedFiles] = useState([]);
    const [resizedImages, setResizedImages] = useState([]);
    const [dimensions, setDimensions] = useState({ width: '', height: '' });
    const [keepAspectRatio, setKeepAspectRatio] = useState(false);
    const [originalDimensions, setOriginalDimensions] = useState({ width: '', height: '' });

    const navigate = useNavigate();
    const handleNavigation = useCallback((path) => navigate(path), [navigate]);

    const handleDownloadAllImages = useCallback(async () => {
        const zip = new JSZip();
        const imagePromises = resizedImages.map(async (image) => {
            const imgBlob = await new Promise((resolve) => {
                const canvas = new OffscreenCanvas(image.width, image.height);
                const ctx = canvas.getContext('2d');
                const img = new Image();
                img.onload = () => {
                    ctx.drawImage(img, 0, 0, image.width, image.height);
                    canvas.convertToBlob({ type: 'image/jpeg' }).then(resolve);
                };
                img.src = URL.createObjectURL(
                    selectedFiles.find((file) => file.name === image.name)
                );
            });
            zip.file(image.name, imgBlob);
        });

        await Promise.all(imagePromises);
        const content = await zip.generateAsync({ type: 'blob' });
        saveAs(content, 'resized-images.zip');
    }, [resizedImages, selectedFiles]);

    const handleFileChange = useCallback((event) => {
        const files = Array.from(event.target.files);
        const validFiles = files.filter((file) => /\.(jpg|jpeg|png)$/i.test(file.name));

        if (validFiles.length > 0) {
            setSelectedFiles((prevFiles) => [...prevFiles, ...validFiles]);
        } else {
            alert('Only .jpg, .jpeg, and .png files are allowed.');
        }
    }, []);

    const handleResizeImages = useCallback(() => {
        if (!dimensions.width || !dimensions.height) {
            alert('Please enter both width and height!');
            return;
        }

        const resized = selectedFiles.map((file) => ({
            name: file.name,
            width: dimensions.width,
            height: dimensions.height,
        }));

        setResizedImages(resized);
    }, [dimensions, selectedFiles]);

    const handleReset = useCallback(() => {
        setSelectedFiles([]);
        setResizedImages([]);
        setDimensions({ width: '', height: '' });
        setOriginalDimensions({ width: '', height: '' });
    }, []);

    const handleDragAndDrop = useCallback((event) => {
        event.preventDefault();
        setDragActive(false);

        const files = Array.from(event.dataTransfer.files);
        handleFileChange({ target: { files } });
    }, [handleFileChange]);

    useEffect(() => {
        if (keepAspectRatio && originalDimensions.width && originalDimensions.height) {
            const aspectRatio = originalDimensions.width / originalDimensions.height;
            if (dimensions.width) {
                setDimensions((prev) => ({
                    ...prev,
                    height: Math.round(dimensions.width / aspectRatio),
                }));
            } else if (dimensions.height) {
                setDimensions((prev) => ({
                    ...prev,
                    width: Math.round(dimensions.height * aspectRatio),
                }));
            }
        }
    }, [keepAspectRatio, dimensions.width, dimensions.height, originalDimensions]);

    return (
        <div className="resize-page">
            <nav className="navbar">
                <div className="navbar-brand">
                    <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
                </div>
                <div className="navbar-links">
                    <button onClick={() => handleNavigation('/dashboard')}>Dashboard</button>
                    <button onClick={() => handleNavigation('/resize')}>Resize</button>
                    <button onClick={() => handleNavigation('/aboutus')}>About Us</button>
                    <button onClick={() => handleNavigation('/contactus')}>Contact Us</button>
                </div>
                <div className="navbar-profile" onClick={() => handleNavigation('/profile')}>
                    <img src={userProfileImg} alt="User Profile" className="user-profile" />
                </div>
            </nav>

            <div className="resize-content">
                <h1>Resize Image</h1>
                <p>Resize JPG, PNG images by defining new width and height in pixels.</p>
                <div
                    className={`file-dropzone ${dragActive ? 'drag-active' : ''}`}
                    onDragOver={(e) => {
                        e.preventDefault();
                        setDragActive(true);
                    }}
                    onDragLeave={() => setDragActive(false)}
                    onDrop={handleDragAndDrop}
                >
                    <p>Drag & Drop images or</p>
                    <input
                        type="file"
                        multiple
                        accept=".jpg,.jpeg,.png"
                        onChange={handleFileChange}
                    />
                </div>

                <div className="resize-settings">
                    <input
                        type="number"
                        placeholder="Width (px)"
                        value={dimensions.width}
                        onChange={(e) => setDimensions((prev) => ({ ...prev, width: e.target.value }))}
                    />
                    <input
                        type="number"
                        placeholder="Height (px)"
                        value={dimensions.height}
                        onChange={(e) => setDimensions((prev) => ({ ...prev, height: e.target.value }))}
                    />
                    <label>
                        <input
                            type="checkbox"
                            checked={keepAspectRatio}
                            onChange={() => setKeepAspectRatio(!keepAspectRatio)}
                        />
                        Keep Aspect Ratio
                    </label>
                </div>

                <div className="actions">
                    <button onClick={handleReset}>Reset</button>
                    <button onClick={handleResizeImages}>Resize</button>
                    <button onClick={handleDownloadAllImages}>Download All</button>
                </div>

                {resizedImages.length > 0 && (
                    <div className="resized-images">
                        {resizedImages.map((image, index) => (
                            <div key={index}>
                                <p>{image.name}</p>
                                <p>
                                    {image.width}px x {image.height}px
                                </p>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Resize;
