import React, { useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import "./BruiseAreaCalculation.css";
import mangoLogo from "../../assets/Logo_white.png";
import userProfileImg from "../../assets/profile.jpg";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";

const BruiseAreaCalculation = () => {
  const [dragActive, setDragActive] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [crop, setCrop] = useState({ unit: "%", width: 50, height: 50 });
  const [imageUrl, setImageUrl] = useState(null);
  const [currentFile, setCurrentFile] = useState(null);
  const navigate = useNavigate();

  const handleAboutUs = useCallback(() => navigate("/aboutuspage"), [navigate]);
  const handleContactUs = useCallback(
    () => navigate("/contactuspage"),
    [navigate]
  );
  const handleUserProfile = useCallback(
    () => navigate("/userprofilepage"),
    [navigate]
  );
  const handleDashboard = useCallback(
    () => navigate("/dashboardpage"),
    [navigate]
  );
  const handleFeatureAnalysis = useCallback(
    () => navigate("/featureanalysis"),
    [navigate]
  );
  const handleResize = useCallback(() => navigate("/resize"), [navigate]);

  const handleFileChange = useCallback((event) => {
    const file = event.target.files[0];
    if (file && /\.(jpg|jpeg|png)$/i.test(file.name)) {
      setSelectedFiles([file]);
      setImageUrl(URL.createObjectURL(file));
    } else {
      alert("Only .jpg, .jpeg, and .png files are allowed.");
    }
  }, []);

  const handleDragOver = useCallback((event) => {
    event.preventDefault();
    setDragActive(true);
  }, []);

  const handleDragLeave = useCallback(() => {
    setDragActive(false);
  }, []);

  const handleDrop = useCallback((event) => {
    event.preventDefault();
    setDragActive(false);
    const file = event.dataTransfer.files[0];
    if (file && /\.(jpg|jpeg|png)$/i.test(file.name)) {
      setSelectedFiles([file]);
      setImageUrl(URL.createObjectURL(file));
    } else {
      alert("Only .jpg, .jpeg, and .png files are allowed.");
    }
  }, []);

  const handleFileDelete = useCallback(() => {
    setSelectedFiles([]);
    setImageUrl(null);
  }, []);

  const handleCropComplete = (crop) => {
    setCrop(crop);
  };

  const handleUpload = useCallback(() => {
    const croppedImage = document.createElement("canvas");
    const image = document.createElement("img");
    image.src = imageUrl;
    image.onload = () => {
      const canvas = croppedImage.getContext("2d");
      canvas.drawImage(
        image,
        crop.x,
        crop.y,
        crop.width,
        crop.height,
        0,
        0,
        crop.width,
        crop.height
      );
      const croppedFile = croppedImage.toDataURL();
      console.log(croppedFile); // You can handle the cropped image as needed
      navigate("/showareacalculation");
    };
  }, [crop, imageUrl, navigate]);

  return (
    <div className="bruiseareacalculation-page">
      <nav className="navbar">
        <div className="navbar-brand">
          <img src={mangoLogo} alt="Mango Logo" className="mango-logo" />
        </div>
        <div className="navbar-links">
          <button className="navbar-link" onClick={handleDashboard}>
            Dashboard
          </button>
          <button className="navbar-link">Bruised Area Calculation</button>
          <button className="navbar-link" onClick={handleFeatureAnalysis}>
            Feature Analysis
          </button>
          <button className="navbar-link" onClick={handleResize}>
            Resize
          </button>
          <button className="navbar-link" onClick={handleAboutUs}>
            About Us
          </button>
          <button className="navbar-link" onClick={handleContactUs}>
            Contact Us
          </button>
        </div>
        <div className="navbar-profile" onClick={handleUserProfile}>
          <img
            src={userProfileImg}
            alt="User Profile"
            className="user-profile"
          />
        </div>
      </nav>

      <div className="bruiseareacalculation-content">
        <h1 className="bruiseareacalculation-title">Bruise Area Calculation</h1>
        <p className="bruise-description">
          The Bruise area calculation will show the total area of mango and the
          total bruise area of mango.
        </p>

        <div className="file-upload-container">
          <div
            className={`file-dropzone ${dragActive ? "drag-active" : ""}`}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
          >
            <p>Drag & Drop your images here or</p>
            <input
              type="file"
              className="file-input"
              id="file-upload"
              onChange={handleFileChange}
              accept=".jpg,.jpeg,.png"
            />
            <label
              htmlFor="file-upload"
              className="browse-bruiseareacalculation-btn"
            >
              Browse 📁
            </label>
            <p>🚨Only .jpg .jpeg .png files are allowed</p>
          </div>

          {imageUrl && (
            <div className="image-cropper">
              <ReactCrop
                src={imageUrl}
                crop={crop}
                onChange={setCrop}
                onComplete={handleCropComplete}
              />
            </div>
          )}

          <div className="selected-files-box">
            <h3>Selected Files:</h3>
            <ul>
              {selectedFiles.map((file, index) => (
                <li key={index} className="file-item">
                  {file.name} ({(file.size / 1024).toFixed(2)} KB)
                  <button className="delete-btn" onClick={handleFileDelete}>
                    ❌
                  </button>
                </li>
              ))}
            </ul>
          </div>
        </div>

        <div className="action-buttons">
          <button className="bt backto-bt" onClick={handleDashboard}>
            Back
          </button>
          <button
            className="bt upload-bruiseareacalculation-bt"
            onClick={handleUpload}
          >
            Upload
          </button>
        </div>
      </div>

      <footer className="footer-bruiseareacalculation">
        <div className="footer-address-bruiseareacalculation">
          <p>
            Mae Fah Luang University 333 Moo 1, Thasud, Muang, Chiang Rai 57100
          </p>
        </div>
      </footer>
    </div>
  );
};

export default BruiseAreaCalculation;
